/* =========================
   style.css
   ========================= */

:root {
    --bg-img: url('/static/images/bg.jpg');

    /* Cyberpunk palette */
    --neon-pink: #ff2fdc;
    --neon-cyan: #00fff0;
    --neon-purple: #7f5cff;
    --neon-yellow: #ffe44d;
    --dark-core: #050508;
    --glass: rgba(5, 5, 15, 0.75);
}

html {
    background-image: 
        radial-gradient(circle at 20% 30%, rgba(255,47,220,0.15), transparent 40%),
        radial-gradient(circle at 80% 70%, rgba(0,255,240,0.12), transparent 45%),
        var(--bg-img);
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-size: cover;
    color: #e6e6eb;
    font-family: math;
    image-rendering: optimizeQuality;
    text-shadow: 0 0 6px rgba(0,255,240,0.15);
}

button {
  margin-top: 10px;
  width: 50%;
  padding: 10px;
  background: linear-gradient(135deg, transparent, rgba(255,47,220,0.15));
  color: var(--neon-cyan);
  font-size: 16px;
  cursor: pointer;
  transition: 0.25s;
  text-align: center;
  float: left;
  border: 1px solid var(--neon-cyan);
  box-shadow:
    0 0 6px rgba(0,255,240,0.4),
    inset 0 0 10px rgba(0,255,240,0.15);
}

button:hover {
  background: var(--neon-cyan);
  color: #050508;
  box-shadow:
    0 0 14px rgba(0,255,240,0.9),
    0 0 40px rgba(0,255,240,0.4);
}

a {
  color: var(--neon-yellow);
  text-shadow: 0 0 6px rgba(255,228,77,0.6);
}

.flash-wrap {
  position: fixed;
  bottom: 66px;
  right: 40px;
  z-index: 60;
}

.flash {
  padding: 8px 12px;
  border-radius: 8px;
  background: var(--glass);
  margin-bottom: 6px;
  opacity: 0;
  transform: translateX(100%);
  animation: slideIn .4s forwards, fadeOut .4s 5s forwards;
  cursor: pointer;
  border: 1px dashed var(--neon-purple);
  box-shadow:
    0 0 12px rgba(127,92,255,0.6),
    inset 0 0 12px rgba(255,47,220,0.15);
}

.error {
  color: #ff3b3b;
  text-shadow: 0 0 10px rgba(255,59,59,0.8);
}

.success {
  color: #00ff88;
  text-shadow: 0 0 10px rgba(0,255,136,0.8);
}

@keyframes slideIn {
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes fadeOut {
  to {
    opacity: 0;
    transform: translateX(100%);
  }
}

footer {
  position: fixed;
  bottom: 0;
  left: 0;
  width: 100%;
  text-align: center;
  padding: 4px 0;
  font-size: 14px;
  background: linear-gradient(
    90deg,
    rgba(255,47,220,0.15),
    rgba(0,255,240,0.15)
  );
  border-top: 1px solid var(--neon-cyan);
  box-shadow:
    0 -2px 20px rgba(0,255,240,0.4);
  backdrop-filter: blur(6px);
}

footer a {
  color: var(--neon-cyan);
}

footer a:hover {
  text-decoration: underline;
  color: var(--neon-pink);
  text-transform: uppercase;
  text-shadow: 0 0 10px rgba(255,47,220,0.9);
}

nav {
  text-align: center;
  padding: 8px;
  font-size: 1rem;
  margin-top: 20px;
}

nav ul {
  list-style-type: none;
  padding: 0;
  margin: 0;
}

nav ul li {
  display: inline;
  margin: 0 8px;
}

nav ul li a {
  color: #9aa0ff;
  text-decoration: none;
  letter-spacing: 1px;
  text-shadow: 0 0 6px rgba(127,92,255,0.6);
}

nav ul li a:hover {
  text-decoration: underline;
  color: var(--neon-cyan);
  text-transform: uppercase;
  text-shadow: 0 0 12px rgba(0,255,240,0.9);
}

.center {
  text-align: center;
}

/* Mobile-specific adjustments */
@media (max-width: 600px) {
  nav ul li {
    display: block;
    margin: 4px 0;
  }
}
